import styled from '@emotion/styled';
import { useContext, useEffect } from 'react';
import { SearchContext } from '../../context/SearchContext';
import { SocketContext } from '../../context/SocketContext';
import useLocalStorage from '../../hooks/useLocalStorage';

const Wrapper = styled.div`
	display: flex;
	width: 100%;
	height: 42px;
	padding: 0px 0px 0px 16px;
	border-radius: 6px;
	position: relative;
	-webkit-box-align: center;
	align-items: center;
	background-color: rgb(244, 244, 244);
	box-shadow: rgb(247 247 247);
`;

const Input = styled.input`
	width: 100%;
	border: none;
	outline: none;
	font-size: 16px;
	letter-spacing: -0.05em;
	background-color: rgb(244, 244, 244);
	color: #333;
	padding-right: 40px;
	caret-color: rgb(95, 0, 128);
`;

const SearchContainer = styled.div`
	display: flex;
`;

const SearchBtn = styled.button`
	position: relative;
	width: 30px;
	height: 30px;
	margin-right: 7px;
	background: url(data:image/svg+xml;base64,)
		50% 50% no-repeat;
	border-radius: 0;
	font-size: 14px;
	color: #333;
	text-transform: none;
	overflow: visible;
	background-color: transparent;
	border: none;
`;

const DeleteAllBtn = styled.button`
	position: absolute;
	top: 50%;
	transform: translateY(-50%);
	width: 16px;
	height: 16px;
	right: 47px;
	margin-left: 20px;
	background: url(data:image/svg+xml;base64,)
		0% 0% / 16px 16px no-repeat transparent;
	border-width: initial;
	border-style: none;
	border-color: initial;
	border-image: initial;
`;

const CancelBtn = styled.button`
	width: 56px;
	height: 21px;
	margin: 11px 2px;
	font-size: 16px;
	line-height: 1.31;
	text-align: right;
	letter-spacing: normal;
	color: rgb(51, 51, 51);
	overflow: visible;
	background-color: transparent;
	border: none;
`;

export default function SearchInput() {
	const socket = useContext(SocketContext);

	const { input, setInput } = useContext(SearchContext);
	const [recentKeywords, setRecentKeywords] = useLocalStorage(
		'recentKeywords',
		[],
		'array'
	);

	const addRecentKeyword = (keyword) => {
		if (recentKeywords.includes(keyword)) {
			return;
		}
		setRecentKeywords([...recentKeywords, keyword]);
		console.log(recentKeywords);
	};

	useEffect(() => {
		socket.emit('search', input.toString());
	}, [input]);

	return (
		<SearchContainer>
			<Wrapper>
				<Input
					type="text"
					placeholder="검색어를 입력해 주세요"
					value={input}
					onChange={(e) => {
						setInput(e.target.value);
					}}
				/>
				{input !== '' && (
					<DeleteAllBtn onClick={() => setInput('')}></DeleteAllBtn>
				)}
				<SearchBtn
					onClick={(e) => {
						addRecentKeyword(input);
					}}
				/>
			</Wrapper>
			{input !== '' && (
				<CancelBtn
					onClick={() => {
						setInput('');
					}}
				>
					취소
				</CancelBtn>
			)}
		</SearchContainer>
	);
}
